import React, { useState } from 'react';
import { getUsers, getVisitors, addVisitor, getVisits, addVisit, addUser } from '../utils/LocalStorageService';
import './VisitorCheckInPage.css';

// Add default users
addUser({ id: 3, name: 'Interviewer', email: 'interviewer@example.com', role: 'host', password: 'interview123' });
addUser({ id: 4, name: 'Director', email: 'director@example.com', role: 'host', password: 'director123' });
addUser({ id: 5, name: 'HR', email: 'hr@example.com', role: 'host', password: 'hr123' });
addUser({ id: 6, name: 'Manager', email: 'manager@example.com', role: 'host', password: 'manager123' });
addUser({ id: 7, name: 'CEO', email: 'ceo@example.com', role: 'host', password: 'ceo123' });

const generateId = () => Math.floor(Math.random() * 1000000);

const VisitorCheckInPage = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    hostName: '',
    reason: ''
  });
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setMessage('');
    setError('');

    const { name, email, phone, hostName, reason } = formData;
    if (!name || !email || !hostName || !reason) {
      setError('Please fill in all required fields');
      return;
    }

    // Find or create visitor
    let visitors = getVisitors();
    let visitor = visitors.find(v => v.email === email);
    if (!visitor) {
      visitor = { id: generateId(), name, email, phone };
      addVisitor(visitor);
    }

    // Find host user by name
    const users = getUsers();
    const host = users.find(u => u.name === hostName && u.role === 'host');
    if (!host) {
      setError('Host not found');
      return;
    }

    // Create visit record
    const visits = getVisits();
    const visit = {
      id: generateId(),
      visitor_id: visitor.id,
      host_id: host.id,
      reason,
      check_in_time: new Date().toISOString(),
      check_out_time: null,
      qr_code: null
    };
    addVisit(visit);

    setMessage('Check-in successful!');
    setFormData({ name: '', email: '', phone: '', hostName: '', reason: '' });
  };

  return (
    <div className="visitor-checkin-container">
      <h2 className="visitor-checkin-title">Visitor Check-In</h2>
      <form className="visitor-checkin-form" onSubmit={handleSubmit}>
        <div>
          <label>Full Name:</label><br />
          <input name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div>
          <label>Email:</label><br />
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </div>
        <div>
          <label>Phone Number:</label><br />
          <input name="phone" value={formData.phone} onChange={handleChange} />
        </div>
        <div>
          <label>Host Name:</label><br />
          <input name="hostName" value={formData.hostName} onChange={handleChange} required />
        </div>
        <div>
          <label>Reason for Visit:</label><br />
          <textarea name="reason" value={formData.reason} onChange={handleChange} required />
        </div>
        {message && <p className="visitor-checkin-success">{message}</p>}
        {error && <p className="visitor-checkin-error">{error}</p>}
        <button type="submit">Check In</button>
      </form>
    </div>
  );
};

export default VisitorCheckInPage;
