import React, { useState, useRef, useEffect } from 'react';
import { checkInVisitor } from '../utils/apiService';
import '../styles/VisitorCheckInPage.css';
import { useNavigate, useLocation } from 'react-router-dom';

// Replace with your actual backend URL
const OCR_API_URL = 'http://127.0.0.1:8000/extract-id-number';

const VisitorCheckInPage = () => {
  const { state } = useLocation();
  const initialHostName = state?.hostName || '';
  
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    hostName: initialHostName,
    reason: '',
    itemsCarried: '',
    photo: null,
    idCardPhoto: null,
    idCardNumber: ''
  });
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [isCameraOn, setIsCameraOn] = useState(false);
  const [isIdCardCameraOn, setIsIdCardCameraOn] = useState(false);
  const videoRef = useRef(null);
  const idCardVideoRef = useRef(null);
  const canvasRef = useRef(null);
  const idCardCanvasRef = useRef(null);
  const navigate = useNavigate();

  const extractIdNumberFromImage = async (imageDataUrl) => {
    if (!imageDataUrl) {
      setError('No ID card photo available to extract.');
      return;
    }
    try {
      const base64 = imageDataUrl.split(',')[1];
      const byteCharacters = atob(base64);
      const byteNumbers = new Array(byteCharacters.length);
      for (let i = 0; i < byteCharacters.length; i++) {
        byteNumbers[i] = byteCharacters.charCodeAt(i);
      }
      const byteArray = new Uint8Array(byteNumbers);
      const blob = new Blob([byteArray], { type: 'image/jpeg' });

      const formData = new FormData();
      formData.append('file', blob, 'idcard.jpg');

      const response = await fetch(OCR_API_URL, {
        method: 'POST',
        body: formData
      });
      if (!response.ok) throw new Error('OCR API error');
      const data = await response.json();
      let number = '';
      if (data.Aadhar && data.Aadhar.length > 0) number = data.Aadhar[0];
      else if (data.PAN && data.PAN.length > 0) number = data.PAN[0];
      else if (data['General Numbers'] && data['General Numbers'].length > 0) number = data['General Numbers'][0];
      if (number) {
        setFormData(prev => ({ ...prev, idCardNumber: number }));
        setMessage('ID Card Number extracted successfully!');
      } else {
        setError('No valid ID number found in the image.');
      }
    } catch (err) {
      setError('Failed to extract ID number: ' + err.message);
    }
  };

  useEffect(() => {
    if (isCameraOn) {
      startCamera();
    } else if (!isCameraOn && videoRef.current && videoRef.current.srcObject) {
      stopCamera();
    }

    if (isIdCardCameraOn) {
      startIdCardCamera();
    } else if (!isIdCardCameraOn && idCardVideoRef.current && idCardVideoRef.current.srcObject) {
      stopIdCardCamera();
    }
    return () => {
      stopCamera();
      stopIdCardCamera();
    };
  }, [isCameraOn, isIdCardCameraOn]);

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
    } catch (err) {
      setError('Failed to access camera for photo: ' + err.message);
      setIsCameraOn(false);
    }
  };

  const stopCamera = () => {
    if (videoRef.current && videoRef.current.srcObject) {
      const tracks = videoRef.current.srcObject.getTracks();
      tracks.forEach(track => track.stop());
      videoRef.current.srcObject = null;
    }
  };

  const startIdCardCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (idCardVideoRef.current) {
        idCardVideoRef.current.srcObject = stream;
      }
    } catch (err) {
      setError('Failed to access camera for ID card: ' + err.message);
      setIsIdCardCameraOn(false);
    }
  };

  const stopIdCardCamera = () => {
    if (idCardVideoRef.current && idCardVideoRef.current.srcObject) {
      const tracks = idCardVideoRef.current.srcObject.getTracks();
      tracks.forEach(track => track.stop());
      idCardVideoRef.current.srcObject = null;
    }
  };

  const handleCapture = () => {
    if (canvasRef.current && videoRef.current) {
      const context = canvasRef.current.getContext('2d');
      canvasRef.current.width = videoRef.current.videoWidth;
      canvasRef.current.height = videoRef.current.videoHeight;
      context.drawImage(videoRef.current, 0, 0, canvasRef.current.width, canvasRef.current.height);
      const imageDataUrl = canvasRef.current.toDataURL('image/jpeg');
      setFormData(prev => ({ ...prev, photo: imageDataUrl }));
      stopCamera();
      setIsCameraOn(false);
    }
  };

  const handleIdCardCapture = () => {
    if (idCardCanvasRef.current && idCardVideoRef.current) {
      const context = idCardCanvasRef.current.getContext('2d');
      idCardCanvasRef.current.width = idCardVideoRef.current.videoWidth;
      idCardCanvasRef.current.height = idCardVideoRef.current.videoHeight;
      context.drawImage(idCardVideoRef.current, 0, 0, idCardCanvasRef.current.width, idCardCanvasRef.current.height);
      const imageDataUrl = idCardCanvasRef.current.toDataURL('image/jpeg');
      console.log('Captured ID Card Image:', imageDataUrl);
      setFormData(prev => ({ ...prev, idCardPhoto: imageDataUrl }));
      stopIdCardCamera();
      setIsIdCardCameraOn(false);
    }
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'photo' && files && files[0]) {
      const reader = new FileReader();
      reader.onload = () => {
        console.log('Uploaded Photo:', reader.result);
        setFormData(prev => ({ ...prev, photo: reader.result }));
      };
      reader.readAsDataURL(files[0]);
    } else if (name === 'idCardPhoto' && files && files[0]) {
      const reader = new FileReader();
      reader.onload = () => {
        console.log('Uploaded ID Card Photo:', reader.result);
        setFormData(prev => ({ ...prev, idCardPhoto: reader.result }));
      };
      reader.readAsDataURL(files[0]);
    } else if (name !== 'hostName') {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setError('');
    console.log('Form Data:', formData);

    const { name, email, phone, hostName, reason, itemsCarried, photo, idCardPhoto, idCardNumber } = formData;
    if (!name || !email || !hostName || !reason || !idCardNumber) {
      setError('Please fill in all required fields.');
      return;
    }

    try {
      const checkInData = {
        name,
        email,
        phone,
        hostName,
        reason,
        itemsCarried,
        photo,
        idCardPhoto,
        idCardNumber
      };

      const response = await checkInVisitor(checkInData);
      setMessage('Check-in successful!');
      setFormData({ 
        name: '', 
        email: '', 
        phone: '', 
        hostName: initialHostName,
        reason: '', 
        itemsCarried: '', 
        photo: null, 
        idCardPhoto: null, 
        idCardNumber: '' 
      });
      
      setTimeout(() => {
        navigate('/HostDashboardPage');
      }, 200);
    } catch (error) {
      setError(error.message || 'Check-in failed.');
    }
  };

  return (
    <div className="visitor-checkin-container">
      <h2 className="visitor-checkin-title">Visitor Check-In</h2>
      <form className="visitor-checkin-form" onSubmit={handleSubmit}>
        <div>
          <label>Full Name:</label><br />
          <input name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div>
          <label>Email:</label><br />
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </div>
        <div>
          <label>Phone Number:</label><br />
          <input name="phone" value={formData.phone} onChange={handleChange} />
        </div>
        <div>
          <label>Host Name:</label><br />
          <input
            name="hostName"
            value={formData.hostName}
            readOnly={true}
            required
            style={{ backgroundColor: '#f0f5f0', cursor: 'not-allowed' }}
          />
        </div>
        <div>
          <label>Reason for Visit:</label>
          <textarea name="reason" value={formData.reason} onChange={handleChange} required />
        </div>
        <div>
          <label>Items/Belongings Being Carried:</label><br />
          <textarea name="itemsCarried" value={formData.itemsCarried} onChange={handleChange} />
        </div>
        <div>
          <label>ID Card Number:</label><br />
          <input name="idCardNumber" value={formData.idCardNumber} onChange={handleChange} required />
        </div>
        <div>
          <label>Take Visitor Photo:</label><br />
          <button
            type="button"
            onClick={() => setIsCameraOn(true)}
            style={{ marginRight: '10px' }}
          >
            Start Camera
          </button>
          {isCameraOn && (
            <div>
              <video ref={videoRef} autoPlay style={{ width: '300px', height: 'auto' }} />
              <canvas ref={canvasRef} style={{ display: 'none' }} />
              <button type="button" onClick={handleCapture}>Capture</button>
              <button type="button" onClick={() => setIsCameraOn(false)}>Cancel</button>
            </div>
          )}
          {formData.photo && (
            <div style={{ marginTop: 10 }}>
              <img src={formData.photo} alt="Visitor Preview" style={{ maxWidth: '200px', maxHeight: '200px' }} />
            </div>
          )}
          <input
            type="file"
            name="photo"
            accept="image/*"
            capture="camera"
            onChange={handleChange}
            style={{ marginTop: '10px' }}
          />
        </div>
        <div>
          <label>Take Visitor ID Card:</label><br />
          <button
            type="button"
            onClick={() => setIsIdCardCameraOn(true)}
            style={{ marginRight: '10px' }}
          >
            Start IdCard Camera
          </button>
          {isIdCardCameraOn && (
            <div>
              <video ref={idCardVideoRef} autoPlay style={{ width: '300px', height: 'auto' }} />
              <canvas ref={idCardCanvasRef} style={{ display: 'none' }} />
              <button type="button" onClick={handleIdCardCapture}>Capture</button>
              <button type="button" onClick={() => setIsIdCardCameraOn(false)}>Cancel</button>
            </div>
          )}
          {formData.idCardPhoto && (
            <div style={{ marginTop: 10 }}>
              <img src={formData.idCardPhoto} alt="ID Card Preview" style={{ maxWidth: '200px', maxHeight: '200px' }} />
              <button
                type="button"
                style={{ marginTop: '10px' }}
                onClick={() => extractIdNumberFromImage(formData.idCardPhoto)}
              >
                Extract ID Number
              </button>
            </div>
          )}
          <input
            type="file"
            name="idCardPhoto"
            accept="image/*"
            capture="camera"
            onChange={handleChange}
            style={{ marginTop: '10px' }}
          />
        </div>
        {message && <p className="visitor-checkin-success">{message}</p>}
        {error && <p className="visitor-checkin-error">{error}</p>}
        <button type="submit">Check In</button>
      </form>
    </div>
  );
};

export default ScanCard;
