import React, { useEffect, useState, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { getUsers, getVisits, createUser } from '../utils/apiService';
import '../styles/AdminDashboardPage.css';

const AdminDashboardPage = () => {
  const [users, setUsers] = useState([]);
  const [visits, setVisits] = useState([]);
  const [filters, setFilters] = useState({
    startDate: '',
    endDate: '',
    hostName: '',
    visitorName: ''
  });
  const [newUser, setNewUser] = useState({
    name: '',
    email: '',
    password: '',
    role: 'host'
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [activeSection, setActiveSection] = useState('dashboard');

  // Fetch users
  useEffect(() => {
    const fetchAllUsers = async () => {
      try {
        const usersData = await getUsers();
        setUsers(usersData);
      } catch (err) {
        setError('Failed to load users. Please try again later.');
        console.error(err);
      }
    };
    fetchAllUsers();
  }, []);

  // Fetch visits
  const fetchVisits = useCallback(async () => {
    setLoading(true);
    setError('');
    try {
      const activeFilters = Object.fromEntries(
        Object.entries(filters).filter(([_, v]) => v)
      );
      const visitsData = await getVisits(activeFilters);
      setVisits(visitsData);
    } catch (err) {
      setError('Failed to load visit logs.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  }, [filters]);

  useEffect(() => {
    fetchVisits();
  }, [fetchVisits]);

  const handleFilterChange = (e) => {
    setFilters(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleUserInputChange = (e) => {
    setNewUser(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleCreateUser = async (e) => {
    e.preventDefault();
    try {
      await createUser(newUser);
      setNewUser({ name: '', email: '', password: '', role: 'host' });
      setError('');
      const usersData = await getUsers();
      setUsers(usersData);
      setActiveSection('users');
    } catch (err) {
      setError('Failed to create user. Please try again.');
      console.error(err);
    }
  };

  const handleFilterSubmit = (e) => {
    e.preventDefault();
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
    window.location.reload();
  };

  // Calculate 30-day visitor stats
  const last30DaysVisits = visits.filter(visit => {
    const visitDate = new Date(visit.check_in_time);
    const thirtyDaysAgo = new Date();
    thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);
    return visitDate >= thirtyDaysAgo;
  });

  return (
    <div className="admin-dashboard-bg">
      <nav className="navbar">
        <div className="navbar-logo">Visitor Management</div>
        <ul className="navbar-links">
          <li><Link to="/">Home</Link></li>
          <li><Link to="/products">Products</Link></li>
          <li><Link to="/resources">Resources</Link></li>
          <li><Link to="/aboutus">About Us</Link></li>
          <li><Link to="/bookademo">Book a Demo</Link></li>
          <li><Link to="/contactus">Contact Us</Link></li>
          <li><button onClick={handleLogout} className="login-btn">Logout</button></li>
        </ul>
      </nav>
      <div className="admin-dashboard-wrapper">
        <aside className="admin-sidebar">
          <h3 className="sidebar-title">Admin Panel</h3>
          <ul className="sidebar-menu">
            <li>
              <button 
                className={activeSection === 'dashboard' ? 'active' : ''} 
                onClick={() => setActiveSection('dashboard')}
              >
                Dashboard
              </button>
            </li>
            <li>
              <button 
                className={activeSection === 'users' ? 'active' : ''} 
                onClick={() => setActiveSection('users')}
              >
                Create Users
              </button>
            </li>
            <li>
              <button 
                className={activeSection === 'visitors' ? 'active' : ''} 
                onClick={() => setActiveSection('visitors')}
              >
                Show Visitors
              </button>
            </li>
          </ul>
        </aside>
        <div className="admin-dashboard-container">
          <h2 className="admin-dashboard-title">Admin Dashboard</h2>
          {error && <p className="admin-dashboard-error">{error}</p>}

          {activeSection === 'dashboard' && (
            <section>
              <h3>Dashboard</h3>
              <div className="dashboard-stats">
                <div className="stat-card">
                  <h4>Last 30 Days Visitors</h4>
                  <p>{last30DaysVisits.length}</p>
                </div>
              </div>
              <h4>Recent Visitors</h4>
              {loading ? <p>Loading visits...</p> : (
                <table className="admin-dashboard-table">
                  <thead>
                    <tr>
                      <th>Visitor Name</th>
                      <th>Host Name</th>
                      <th>Check-In Time</th>
                    </tr>
                  </thead>
                  <tbody>
                    {last30DaysVisits.slice(0, 5).map(visit => (
                      <tr key={visit.id}>
                        <td>{visit.visitorName}</td>
                        <td>{visit.hostName}</td>
                        <td>{new Date(visit.check_in_time).toLocaleString()}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </section>
          )}

          {activeSection === 'users' && (
            <section>
              <h3>Create Host User</h3>
              <form className="admin-dashboard-form" onSubmit={handleCreateUser}>
                <label>
                  Name:
                  <input 
                    type="text" 
                    name="name" 
                    value={newUser.name} 
                    onChange={handleUserInputChange} 
                    required 
                  />
                </label>
                <label>
                  Email:
                  <input 
                    type="email" 
                    name="email" 
                    value={newUser.email} 
                    onChange={handleUserInputChange} 
                    required 
                  />
                </label>
                <label>
                  Password:
                  <input 
                    type="password" 
                    name="password" 
                    value={newUser.password} 
                    onChange={handleUserInputChange} 
                    required 
                  />
                </label>
                <button type="submit">Create Host</button>
              </form>
              <h3>Existing Users</h3>
              {loading ? <p>Loading users...</p> : (
                <table className="admin-dashboard-table">
                  <thead>
                    <tr>
                      <th>Name</th>
                      <th>Email</th>
                      <th>Role</th>
                    </tr>
                  </thead>
                  <tbody>
                    {users.length === 0 ? (
                      <tr><td colSpan="3">No users found</td></tr>
                    ) : (
                      users.map(user => (
                        <tr key={user.id}>
                          <td>{user.name}</td>
                          <td>{user.email}</td>
                          <td>{user.role}</td>
                        </tr>
                      ))
                    )}
                  </tbody>
                </table>
              )}
            </section>
          )}

          {activeSection === 'visitors' && (
            <section className="admin-dashboard-section">
              <h3>Visitor Logs</h3>
              <form className="admin-dashboard-filter-form" onSubmit={handleFilterSubmit}>
                <label>
                  Start Date:
                  <input type="date" name="startDate" value={filters.startDate} onChange={handleFilterChange} />
                </label>
                <label>
                  End Date:
                  <input type="date" name="endDate" value={filters.endDate} onChange={handleFilterChange} />
                </label>
                <label>
                  Host Name:
                  <input name="hostName" value={filters.hostName} onChange={handleFilterChange} />
                </label>
                <label>
                  Visitor Name:
                  <input name="visitorName" value={filters.visitorName} onChange={handleFilterChange} />
                </label>
                <button type="submit">Filter</button>
              </form>
              {loading ? <p>Loading visits...</p> : (
                <table className="admin-dashboard-table">
                  <thead>
                    <tr>
                      <th>Visitor Name</th>
                      <th>Visitor Email</th>
                      <th>Photo</th>
                      <th>Host Name</th>
                      <th>Reason</th>
                      <th>Check-In Time</th>
                      <th>Check-Out Time</th>
                    </tr>
                  </thead>
                  <tbody>
                    {visits.length === 0 ? (
                      <tr><td colSpan="7">No visits found for the selected filters.</td></tr>
                    ) : (
                      visits.map(visit => (
                        <tr key={visit.id}>
                          <td>{visit.visitorName}</td>
                          <td>{visit.visitorEmail}</td>
                          <td>
                            {visit.visitorPhoto && <img src={visit.visitorPhoto} alt="Visitor" style={{ maxWidth: '80px', borderRadius: '4px' }} />}
                          </td>
                          <td>{visit.hostName}</td>
                          <td>{visit.reason}</td>
                          <td>{new Date(visit.check_in_time).toLocaleString()}</td>
                          <td>{visit.check_out_time ? new Date(visit.check_out_time).toLocaleString() : 'Not Checked Out'}</td>
                        </tr>
                      ))
                    )}
                  </tbody>
                </table>
              )}
            </section>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboardPage;
