import React, { useEffect, useState } from 'react';
import { getVisits, getVisitors, getUsers } from '../utils/LocalStorageService';
import './HostDashboardPage.css';

const HostDashboardPage = () => {
  const [visits, setVisits] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    try {
      const hostName = localStorage.getItem('name');
      console.log('Logged-in Host Name:', hostName);
      if (!hostName) {
        setError('No logged-in host. Please log in.');
        return;
      }

      const visitsData = getVisits();
      console.log('All Visits:', visitsData);
      const visitors = getVisitors();
      console.log('All Visitors:', visitors);
      const users = getUsers();
      console.log('All Users:', users);

      const host = users.find(u => u.name.toLowerCase() === hostName.toLowerCase() && u.role === 'host');
      console.log('Found Host:', host);
      if (!host) {
        setError('Host user not found');
        return;
      }

      const hostVisits = visitsData.filter(v => v.host_id === host.id).map(visit => {
        const visitor = visitors.find(vis => vis.id === visit.visitor_id) || {};
        return { ...visit, visitor };
      });
      console.log('Host Visits:', hostVisits);
      setVisits(hostVisits);
    } catch (err) {
      console.error('Error:', err);
      setError('Failed to load visits');
    }
  }, []);

  return (
    <div className="host-dashboard-container">
      <h2 className="host-dashboard-title">Host Dashboard - Your Visitors</h2>
      {error && <p className="host-dashboard-error">{error}</p>}
      <table className="host-dashboard-table">
        <thead>
          <tr>
            <th>Visitor Name</th>
            <th>Email</th>
            <th>Phone</th>
            <th>Reason</th>
            <th>Check-In Time</th>
            <th>Check-Out Time</th>
          </tr>
        </thead>
        <tbody>
          {visits.length === 0 ? (
            <tr><td colSpan="6">No visits found</td></tr>
          ) : (
            visits.map(visit => (
              <tr key={visit.id}>
                <td>{visit.visitor?.name || 'Unknown'}</td>
                <td>{visit.visitor?.email || 'Unknown'}</td>
                <td>{visit.visitor?.phone || '-'}</td>
                <td>{visit.reason || '-'}</td>
                <td>{visit.check_in_time ? new Date(visit.check_in_time).toLocaleString() : '-'}</td>
                <td>{visit.check_out_time ? new Date(visit.check_out_time).toLocaleString() : '-'}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default HostDashboardPage;
