import React, { useState, useRef } from 'react';
import { getUsers, getVisitors, addVisitor, getVisits, addVisit } from '../utils/LocalStorageService';
import './VisitorCheckInPage.css';

const generateId = (key) => {
  const items = JSON.parse(localStorage.getItem(key)) || [];
  const maxId = items.length > 0 ? Math.max(...items.map(item => item.id)) : 0;
  return maxId + 1;
};

const VisitorCheckInPage = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    hostName: '',
    reason: '',
    itemsCarried: '',
    photo: null
  });
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [isCameraActive, setIsCameraActive] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  const startCamera = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: 'user' }
      });
      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
        streamRef.current = mediaStream;
        setIsCameraActive(true);
        console.log('Camera started');
      }
    } catch (err) {
      console.error('Error accessing camera:', err);
      setError('Failed to access camera. Please ensure camera permissions are granted.');
    }
  };

  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
      setIsCameraActive(false);
      console.log('Camera stopped');
    }
  };

  const handleCapturePhoto = () => {
    if (!isCameraActive) {
      startCamera();
    } else {
      if (!videoRef.current || !canvasRef.current) {
        setError('Camera not ready');
        return;
      }
      const context = canvasRef.current.getContext('2d');
      canvasRef.current.width = videoRef.current.videoWidth;
      canvasRef.current.height = videoRef.current.videoHeight;
      context.drawImage(videoRef.current, 0, 0);
      const photoData = canvasRef.current.toDataURL('image/jpeg');
      setFormData(prev => ({ ...prev, photo: photoData }));
      console.log('Photo captured:', photoData.substring(0, 50) + '...');
      stopCamera();
    }
  };

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setMessage('');
    setError('');

    const { name, email, phone, hostName, reason, itemsCarried, photo } = formData;
    if (!name || !email || !hostName || !reason) {
      setError('Please fill in all required fields');
      return;
    }

    let visitors = getVisitors();
    let visitor = visitors.find(v => v.email.toLowerCase() === email.toLowerCase());
    if (!visitor) {
      visitor = { id: generateId('vms_visitors'), name, email, phone, itemsCarried, photo };
      addVisitor(visitor);
      console.log('Added Visitor:', visitor);
    }

    const users = getUsers();
    const host = users.find(u => u.name.toLowerCase() === hostName.toLowerCase() && u.role === 'host');
    console.log('Found Host:', host);
    if (!host) {
      setError('Host not found');
      return;
    }

    const visit = {
      id: generateId('vms_visits'),
      visitor_id: visitor.id,
      host_id: host.id,
      reason,
      check_in_time: new Date().toISOString(),
      check_out_time: null,
      qr_code: null
    };
    addVisit(visit);
    console.log('Added Visit:', visit);

    setMessage('Check-in successful!');
    setFormData({ name: '', email: '', phone: '', hostName: '', reason: '', itemsCarried: '', photo: null });
  };

  return (
    <div className="visitor-checkin-container">
      <h2 className="visitor-checkin-title">Visitor Check-In</h2>
      <form className="visitor-checkin-form" onSubmit={handleSubmit}>
        <div>
          <label>Full Name:</label><br />
          <input name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div>
          <label>Email:</label><br />
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </div>
        <div>
          <label>Phone Number:</label><br />
          <input name="phone" value={formData.phone} onChange={handleChange} />
        </div>
        <div>
          <label>Host Name:</label><br />
          <input name="hostName" value={formData.hostName} onChange={handleChange} required />
        </div>
        <div>
          <label>Reason for Visit:</label><br />
          <textarea name="reason" value={formData.reason} onChange={handleChange} required />
        </div>
        <div>
          <label>Items/Belongings Being Carried:</label><br />
          <textarea name="itemsCarried" value={formData.itemsCarried} onChange={handleChange} />
        </div>
        <div>
          <label>Take Visitor Photo:</label><br />
          {isCameraActive && (
            <video ref={videoRef} autoPlay style={{ maxWidth: '300px', maxHeight: '300px', marginTop: '10px' }} />
          )}
          <button type="button" onClick={handleCapturePhoto} style={{ marginTop: '10px' }}>
            {isCameraActive ? 'Capture Photo' : 'Start Camera'}
          </button>
          <canvas ref={canvasRef} style={{ display: 'none' }} />
          {formData.photo && (
            <div style={{ marginTop: '10px' }}>
              <img src={formData.photo} alt="Captured Visitor" style={{ maxWidth: '200px', maxHeight: '200px' }} />
            </div>
          )}
        </div>
        {message && <p className="visitor-checkin-success">{message}</p>}
        {error && <p className="visitor-checkin-error">{error}</p>}
        <button type="submit">Check In</button>
      </form>
    </div>
  );
};

export default VisitorCheckInPage;
