import React, { useState } from 'react';
import { getUsers, getVisitors, addVisitor, getVisits, addVisit } from '../utils/LocalStorageService';
import './VisitorCheckInPage.css';

// Utility function to generate unique IDs for visitors and visits
const generateId = (key) => {
  const items = JSON.parse(localStorage.getItem(key)) || [];
  const maxId = items.length > 0 ? Math.max(...items.map(item => item.id)) : 0;
  return maxId + 1;
};

const VisitorCheckInPage = () => {
  // State for form data
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    hostName: '',
    reason: '',
    itemsCarried: '',
    photo: null // Stores base64 string of the photo
  });
  // State for success and error messages
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  // Handle input changes, including photo file
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'photo' && files && files[0]) {
      const reader = new FileReader();
      reader.onload = () => {
        setFormData(prev => ({ ...prev, photo: reader.result }));
      };
      reader.onerror = () => {
        setError('Failed to read photo');
      };
      reader.readAsDataURL(files[0]);
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  // Remove the current photo
  const removePhoto = () => {
    setFormData(prev => ({ ...prev, photo: null }));
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    setMessage('');
    setError('');

    const { name, email, phone, hostName, reason, itemsCarried, photo } = formData;
    // Validate required fields
    if (!name || !email || !hostName || !reason) {
      setError('Please fill in all required fields');
      return;
    }

    // Check for valid email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setError('Please enter a valid email address');
      return;
    }

    // Get or create visitor
    let visitors = getVisitors();
    let visitor = visitors.find(v => v.email.toLowerCase() === email.toLowerCase());
    if (!visitor) {
      visitor = { id: generateId('vms_visitors'), name, email, phone, itemsCarried, photo };
      addVisitor(visitor);
      console.log('Added Visitor:', visitor);
    } else {
      // Update existing visitor
      visitors = visitors.map(v =>
        v.email.toLowerCase() === email.toLowerCase()
          ? { ...v, name, phone, itemsCarried, photo }
          : v
      );
      localStorage.setItem('vms_visitors', JSON.stringify(visitors));
      console.log('Updated Visitor:', visitor);
    }

    // Validate host
    const users = getUsers();
    const host = users.find(u => u.name.toLowerCase() === hostName.toLowerCase() && u.role === 'host');
    if (!host) {
      setError('Host not found');
      return;
    }

    // Create visit record
    const visit = {
      id: generateId('vms_visits'),
      visitor_id: visitor.id,
      host_id: host.id,
      reason,
      check_in_time: new Date().toISOString(),
      check_out_time: null,
      qr_code: `VISITOR_${Date.now()}_${visitor.id}`,
      photo // Store photo in visit record
    };
    addVisit(visit);
    console.log('Added Visit:', visit);

    // Reset form and show success message
    setMessage('Check-in successful!');
    setFormData({ name: '', email: '', phone: '', hostName: '', reason: '', itemsCarried: '', photo: null });
  };

  return (
    <div className="visitor-checkin-container">
      <h2 className="visitor-checkin-title">Visitor Check-In</h2>
      <form className="visitor-checkin-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name">Full Name:</label>
          <input
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            id="email"
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="phone">Phone Number:</label>
          <input
            id="phone"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="hostName">Host Name:</label>
          <input
            id="hostName"
            name="hostName"
            value={formData.hostName}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="reason">Reason for Visit:</label>
          <textarea
            id="reason"
            name="reason"
            value={formData.reason}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="itemsCarried">Items/Belongings Being Carried:</label>
          <textarea
            id="itemsCarried"
            name="itemsCarried"
            value={formData.itemsCarried}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="photoInput">Take Visitor Photo:</label>
          <div className="photo-section">
            {formData.photo ? (
              <div className="photo-preview">
                <img
                  src={formData.photo}
                  alt="Visitor Preview"
                  className="photo-img"
                />
                <div className="photo-actions">
                  <button
                    type="button"
                    className="photo-action-btn"
                    onClick={() => document.getElementById('photoInput').click()}
                  >
                    Retake Photo
                  </button>
                  <button
                    type="button"
                    className="photo-action-btn remove"
                    onClick={removePhoto}
                  >
                    Remove Photo
                  </button>
                </div>
              </div>
            ) : (
              <div className="photo-upload">
                <input
                  id="photoInput"
                  type="file"
                  name="photo"
                  accept="image/*"
                  capture="user"
                  onChange={handleChange}
                />
                <label htmlFor="photoInput" className="photo-upload-label">
                  Choose or Take Photo
                </label>
              </div>
            )}
          </div>
        </div>
        {message && <p className="visitor-checkin-success">{message}</p>}
        {error && <p className="visitor-checkin-error">{error}</p>}
        <button type="submit" className="submit-btn">Check In</button>
      </form>
    </div>
  );
};

export default VisitorCheckInPage;
