import React, { useEffect, useState } from 'react';
import { getUsers, getVisits, getVisitors } from '../utils/LocalStorageService';
import './AdminDashboardPage.css';

const AdminDashboardPage = () => {
  const [users, setUsers] = useState([]);
  const [visits, setVisits] = useState([]);
  const [filters, setFilters] = useState({
    startDate: '',
    endDate: '',
    hostName: '',
    visitorName: ''
  });
  const [error, setError] = useState('');

  useEffect(() => {
    try {
      const usersData = getUsers();
      const visitsData = getVisits();
      const visitors = getVisitors();
      setUsers(usersData);

      // Apply filters
      let filteredVisits = visitsData;

      if (filters.startDate) {
        filteredVisits = filteredVisits.filter(v => new Date(v.check_in_time) >= new Date(filters.startDate));
      }
      if (filters.endDate) {
        filteredVisits = filteredVisits.filter(v => new Date(v.check_in_time) <= new Date(filters.endDate));
      }
      if (filters.hostName) {
        const hostUsers = usersData.filter(u => u.role === 'host' && u.name.toLowerCase().includes(filters.hostName.toLowerCase()));
        const hostIds = hostUsers.map(u => u.id);
        filteredVisits = filteredVisits.filter(v => hostIds.includes(v.host_id));
      }
      if (filters.visitorName) {
        const visitorMatches = visitors.filter(vis => vis.name.toLowerCase().includes(filters.visitorName.toLowerCase()));
        const visitorIds = visitorMatches.map(vis => vis.id);
        filteredVisits = filteredVisits.filter(v => visitorIds.includes(v.visitor_id));
      }

      // Map visits with visitor and host info
      const visitsWithDetails = filteredVisits.map(visit => {
        const visitor = visitors.find(vis => vis.id === visit.visitor_id) || {};
        const host = usersData.find(u => u.id === visit.host_id) || {};
        return { ...visit, visitor, host };
      });

      setVisits(visitsWithDetails);
    } catch (err) {
      setError('Failed to load data');
    }
  }, [filters]);

  const handleFilterChange = (e) => {
    setFilters(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleFilterSubmit = (e) => {
    e.preventDefault();
    // The useEffect will react to filters change and update visits
  };

  return (
    <div className="admin-dashboard-container">
      <h2 className="admin-dashboard-title">Admin Dashboard</h2>
      {error && <p className="admin-dashboard-error">{error}</p>}

      <section>
        <h3>Users</h3>
        <table className="admin-dashboard-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Role</th>
            </tr>
          </thead>
          <tbody>
            {users.length === 0 ? (
              <tr><td colSpan="3">No users found</td></tr>
            ) : (
              users.map(user => (
                <tr key={user.id}>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                  <td>{user.role}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </section>

      <section className="admin-dashboard-section">
        <h3>Visitor Logs</h3>
        <form className="admin-dashboard-filter-form" onSubmit={handleFilterSubmit} style={{ marginBottom: 20 }}>
          <label>
            Start Date:
            <input type="date" name="startDate" value={filters.startDate} onChange={handleFilterChange} />
          </label>
          <label>
            End Date:
            <input type="date" name="endDate" value={filters.endDate} onChange={handleFilterChange} />
          </label>
          <label>
            Host Name:
            <input name="hostName" value={filters.hostName} onChange={handleFilterChange} />
          </label>
          <label>
            Visitor Name:
            <input name="visitorName" value={filters.visitorName} onChange={handleFilterChange} />
          </label>
          <button type="submit">Filter</button>
        </form>

        <table className="admin-dashboard-table">
          <thead>
            <tr>
              <th>Visitor Name</th>
              <th>Visitor Email</th>
              <th>Visitor Phone</th>
              <th>Host Name</th>
              <th>Reason</th>
              <th>Check-In Time</th>
              <th>Check-Out Time</th>
            </tr>
          </thead>
          <tbody>
            {visits.length === 0 ? (
              <tr><td colSpan="7">No visits found</td></tr>
            ) : (
              visits.map(visit => (
                <tr key={visit.id}>
                  <td>{visit.visitor.name}</td>
                  <td>{visit.visitor.email}</td>
                  <td>{visit.visitor.phone || '-'}</td>
                  <td>{visit.host.name}</td>
                  <td>{visit.reason}</td>
                  <td>{new Date(visit.check_in_time).toLocaleString()}</td>
                  <td>{visit.check_out_time ? new Date(visit.check_out_time).toLocaleString() : '-'}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </section>
    </div>
  );
};

export default AdminDashboardPage;
