import React, { useState } from 'react';
import { getUsers, getVisitors, addVisitor, getVisits, addVisit } from '../utils/LocalStorageService';

const generateId = () => Math.floor(Math.random() * 1000000);

const VisitorCheckInPage = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    hostName: '',
    reason: ''
  });
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setMessage('');
    setError('');

    const { name, email, phone, hostName, reason } = formData;
    if (!name || !email || !hostName || !reason) {
      setError('Please fill in all required fields');
      return;
    }

    // Find or create visitor
    let visitors = getVisitors();
    let visitor = visitors.find(v => v.email === email);
    if (!visitor) {
      visitor = { id: generateId(), name, email, phone };
      addVisitor(visitor);
    }

    // Find host user by name
    const users = getUsers();
    // Trim and match case-insensitive for host name
    const host = users.find(u => u.role === 'host' && u.name.toLowerCase() === hostName.toLowerCase());
    if (!host) {
      setError('Host not found');
      return;
    }

    // Create visit record
    const visits = getVisits();
    const visit = {
      id: generateId(),
      visitor_id: visitor.id,
      host_id: host.id,
      reason,
      check_in_time: new Date().toISOString(),
      check_out_time: null,
      qr_code: null
    };
    addVisit(visit);

    setMessage('Check-in successful!');
    setFormData({ name: '', email: '', phone: '', hostName: '', reason: '' });
  };

  return (
    <div style={{ maxWidth: 500, margin: 'auto', padding: 20 }}>
      <h2>Visitor Check-In</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Full Name:</label><br />
          <input name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div>
          <label>Email:</label><br />
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </div>
        <div>
          <label>Phone Number:</label><br />
          <input name="phone" value={formData.phone} onChange={handleChange} />
        </div>
        <div>
          <label>Host Name:</label><br />
          <select name="hostName" value={formData.hostName} onChange={handleChange} required>
            <option value="">Select Host</option>
            <option value="Interviewer">Interviewer</option>
            <option value="HR">HR</option>
            <option value="Manager">Manager</option>
            <option value="Director">Director</option>
            <option value="CEO">CEO</option>
          </select>
        </div>
        <div>
          <label>Reason for Visit:</label><br />
          <textarea name="reason" value={formData.reason} onChange={handleChange} required />
        </div>
        {message && <p style={{ color: 'green' }}>{message}</p>}
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="submit" style={{ marginTop: 10 }}>Check In</button>
      </form>
    </div>
  );
};

export default VisitorCheckInPage;
