import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getVisits, getVisitors, getUsers, saveVisits } from '../utils/LocalStorageService';
import '../styles/HostDashboardPage.css';

const HostDashboardPage = () => {
  const [visits, setVisits] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    try {
      const hostName = localStorage.getItem('name');
      console.log('Logged-in Host Name:', hostName);
      if (!hostName) {
        setError('No logged-in host. Please log in.');
        return;
      }

      const visitsData = getVisits();
      console.log('All Visits:', visitsData);
      const visitors = getVisitors();
      console.log('All Visitors:', visitors);
      const users = getUsers();
      console.log('All Users:', users);

      const host = users.find(u => u.name.toLowerCase() === hostName.toLowerCase() && u.role === 'host');
      console.log('Found Host:', host);
      if (!host) {
        setError('Host user not found');
        return;
      }

      const hostVisits = visitsData.filter(v => v.host_id === host.id).map(visit => {
        const visitor = visitors.find(vis => vis.id === visit.visitor_id) || {};
        console.log(`Visit ID ${visit.id}: Visitor -`, visitor);
        return { ...visit, visitor };
      });
      console.log('Host Visits:', hostVisits);
      setVisits(hostVisits);
    } catch (err) {
      console.error('Error:', err);
      setError('Failed to load visits');
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    localStorage.removeItem('name');
    window.location.reload();
  };

  const handleCheckOut = (visitId) => {
    try {
      const visitsData = getVisits();
      const updatedVisits = visitsData.map(visit =>
        visit.id === visitId && !visit.check_out_time
          ? { ...visit, check_out_time: new Date().toISOString() }
          : visit
      );
      saveVisits(updatedVisits);
      setVisits(prevVisits =>
        prevVisits.map(visit =>
          visit.id === visitId && !visit.check_out_time
            ? { ...visit, check_out_time: new Date().toISOString() }
            : visit
        )
      );
      console.log(`Checked out visit ID ${visitId}`);
    } catch (err) {
      console.error('Error during check-out:', err);
      setError('Failed to check out visitor');
    }
  };

  return (
    <div className="host-dashboard-bg">
      <nav className="navbar">
        <div className="navbar-logo">Visitor Management</div>
        <ul className="navbar-links">
          <li><Link to="/">Home</Link></li>
          <li><Link to="/products">Products</Link></li>
          <li><Link to="/resources">Resources</Link></li>
          <li><Link to="/aboutus">About Us</Link></li>
          <li><Link to="/bookademo">Book a Demo</Link></li>
          <li><Link to="/contactus">Contact Us</Link></li>
          <li><button onClick={handleLogout} className="login-btn" style={{border:'none',background:'none',padding:0,margin:0,cursor:'pointer'}}>Logout</button></li>
        </ul>
      </nav>
      <div className="host-dashboard-container">
        <div className="host-dashboard-header-modern">
          <div>
            <h2 className="host-dashboard-title">Host Dashboard</h2>
            <p className="host-dashboard-subtitle">Your Visitors</p>
          </div>
          <Link to="/checkin" className="add-visitor-btn">
            + Add Visitors
          </Link>
        </div>
        {error && <p className="host-dashboard-error">{error}</p>}
        <div className="host-dashboard-table-card">
          <table className="host-dashboard-table-modern">
            <thead>
              <tr>
                <th>Visitor Name</th>
                <th>Email</th>
                <th>Phone</th>
                <th>Items Carried</th>
                <th>Photo</th>
                <th>Reason</th>
                <th>Check-In Time</th>
                <th>Check-Out Time</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {visits.length === 0 ? (
                <tr><td colSpan="9">No visits found</td></tr>
              ) : (
                visits.map(visit => (
                  <tr key={visit.id}>
                    <td>{visit.visitor?.name || 'Unknown'}</td>
                    <td>{visit.visitor?.email || 'Unknown'}</td>
                    <td>{visit.visitor?.phone || '-'}</td>
                    <td>{visit.visitor?.itemsCarried || '-'}</td>
                    <td>
                      {visit.visitor?.photo ? (
                        <img src={visit.visitor.photo} alt="Visitor" className="host-dashboard-photo" />
                      ) : '-'}
                    </td>
                    <td>{visit.reason || '-'}</td>
                    <td>{visit.check_in_time ? new Date(visit.check_in_time).toLocaleString() : '-'}</td>
                    <td>{visit.check_out_time ? new Date(visit.check_out_time).toLocaleString() : '-'}</td>
                    <td>
                      {!visit.check_out_time ? (
                        <button
                          onClick={() => handleCheckOut(visit.id)}
                          className="host-dashboard-checkout-modern"
                        >
                          Check Out
                        </button>
                      ) : (
                        <span className="host-dashboard-checkedout">Checked Out</span>
                      )}
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default HostDashboardPage;
