// Filename: src/utils/apiService.js
const API_BASE_URL = 'http://localhost:4000/api';

/**
 * A helper function to handle API requests and errors.
 * @param {string} endpoint - The API endpoint to call (e.g., '/login').
 * @param {object} options - The options object for the fetch call (method, headers, body).
 * @returns {Promise<any>} - The JSON response from the server.
 */
const request = async (endpoint, options = {}) => {
  const token = localStorage.getItem('token');
  const headers = {
    'Content-Type': 'application/json',
    ...options.headers,
  };
  if (token) {
    headers['Authorization'] = `Bearer ${token}`;
  }

  console.log(`API Request: ${API_BASE_URL}${endpoint}`, { options, headers });

  try {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, { ...options, headers });
    const responseData = await response.json();

    console.log(`API Response: ${API_BASE_URL}${endpoint}`, responseData);

    if (!response.ok) {
      throw new Error(responseData.message || 'An unknown error occurred.');
    }

    return responseData;
  } catch (error) {
    console.error(`API Error: ${API_BASE_URL}${endpoint}`, error.message);
    throw error;
  }
};

// --- API Functions ---

export const loginUser = (email, password) => {
  return request('/login', {
    method: 'POST',
    body: JSON.stringify({ email, password }),
  });
};

export const registerUser = (userData) => {
  return request('/register', {
    method: 'POST',
    body: JSON.stringify(userData),
  });
};

export const getUsers = () => {
  return request('/users');
};

export const getVisits = (filters = {}) => {
  const params = new URLSearchParams(filters);
  return request(`/visits?${params.toString()}`);
};

export const checkInVisitor = (checkInData) => {
  return request('/visits', {
    method: 'POST',
    body: JSON.stringify(checkInData),
  });
};

export const checkOutVisit = (visitId) => {
  return request(`/visits/${visitId}/checkout`, {
    method: 'PUT',
  });
};
