import React, { useEffect, useState, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { getUsers, getVisits, createUser } from '../utils/apiService';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';


// Register Chart.js components
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const AdminDashboardPage = () => {
  const [users, setUsers] = useState([]);
  const [visits, setVisits] = useState([]);
  const [filters, setFilters] = useState({
    startDate: '',
    endDate: '',
    hostName: '',
    visitorName: ''
  });
  const [newUser, setNewUser] = useState({
    name: '',
    email: '',
    password: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(''); // Added success state
  const [loading, setLoading] = useState(true);
  const [activeSection, setActiveSection] = useState('dashboard');

  useEffect(() => {
    const fetchAllUsers = async () => {
      try {
        const usersData = await getUsers();
        setUsers(usersData);
      } catch (err) {
        setError('Failed to load users. Please try again later.');
        console.error(err);
      }
    };
    fetchAllUsers();
  }, []);

  const fetchVisits = useCallback(async () => {
    setLoading(true);
    setError('');
    try {
      const activeFilters = Object.fromEntries(
        Object.entries(filters).filter(([_, v]) => v)
      );
      const visitsData = await getVisits(activeFilters);
      setVisits(visitsData);
    } catch (err) {
      setError('Failed to load visit logs.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  }, [filters]);

  useEffect(() => {
    fetchVisits();
  }, [fetchVisits]);

  const handleFilterChange = (e) => {
    setFilters(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleUserInputChange = (e) => {
    setNewUser(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleCreateUser = async (e) => {
    e.preventDefault();
    try {
      await createUser(newUser);
      setNewUser({ name: '', email: '', password: '' });
      setError('');
      setSuccess('User created successfully!'); // Set success message
      const usersData = await getUsers();
      setUsers(usersData);
      setActiveSection('users');
      setTimeout(() => setSuccess(''), 3000); // Clear success message after 3 seconds
    } catch (err) {
      setError(err.message || 'Failed to create user. Please try again.');
      console.error(err);
    }
  };

  const handleFilterSubmit = (e) => {
    e.preventDefault();
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
    window.location.reload();
  };

  // Calculate 30-day visitor stats
  const last30DaysVisits = visits.filter(visit => {
    const visitDate = new Date(visit.check_in_time);
    const thirtyDaysAgo = new Date();
    thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);
    return visitDate >= thirtyDaysAgo;
  });

  // Prepare data for 30-day visitor chart
  const getChartData = () => {
    const today = new Date();
    const labels = [];
    const data = [];
    for (let i = 29; i >= 0; i--) {
      const date = new Date();
      date.setDate(today.getDate() - i);
      const dateString = date.toISOString().split('T')[0];
      labels.push(dateString);
      const count = last30DaysVisits.filter(visit => 
        new Date(visit.check_in_time).toISOString().split('T')[0] === dateString
      ).length;
      data.push(count);
    }
    return {
      labels,
      datasets: [
        {
          label: 'Visitors per Day',
          data,
          backgroundColor: '#0984e3',
          borderColor: '#0984e3',
          borderWidth: 1
        }
      ]
    };
  };

  const chartData = getChartData();

  const chartOptions = {
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Number of Visitors'
        }
      },
      x: {
        title: {
          display: true,
          text: 'Date'
        }
      }
    },
    plugins: {
      legend: {
        display: true
      }
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <nav className="bg-white shadow p-4">
        <div className="container mx-auto flex justify-between items-center">
          <div className="text-xl font-bold text-gray-800">Visitor Management</div>
          <ul className="flex space-x-4">
            <li><Link to="/" className="text-blue-600 hover:underline">Home</Link></li>
            <li><Link to="/products" className="text-blue-600 hover:underline">Products</Link></li>
            <li><Link to="/resources" className="text-blue-600 hover:underline">Resources</Link></li>
            <li><Link to="/aboutus" className="text-blue-600 hover:underline">About Us</Link></li>
            <li><Link to="/bookademo" className="text-blue-600 hover:underline">Book a Demo</Link></li>
            <li><Link to="/contactus" className="text-blue-600 hover:underline">Contact Us</Link></li>
            <li><button onClick={handleLogout} className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600">Logout</button></li>
          </ul>
        </div>
      </nav>
      <div className="container mx-auto flex mt-6">
        <aside className="w-1/4 bg-white shadow rounded-lg p-6 mr-6">
          <h3 className="text-lg font-semibold text-gray-800 mb-4">Admin Panel</h3>
          <ul className="space-y-2">
            <li>
              <button 
                className={`w-full text-left px-4 py-2 rounded ${activeSection === 'dashboard' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800 hover:bg-gray-300'}`} 
                onClick={() => setActiveSection('dashboard')}
              >
                Dashboard
              </button>
            </li>
            <li>
              <button 
                className={`w-full text-left px-4 py-2 rounded ${activeSection === 'users' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800 hover:bg-gray-300'}`} 
                onClick={() => setActiveSection('users')}
              >
                Create Users
              </button>
            </li>
            <li>
              <button 
                className={`w-full text-left px-4 py-2 rounded ${activeSection === 'visitors' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800 hover:bg-gray-300'}`} 
                onClick={() => setActiveSection('visitors')}
              >
                Show Visitors
              </button>
            </li>
          </ul>
        </aside>
        <div className="w-3/4 bg-white shadow rounded-lg p-6">
          <h2 className="text-2xl font-bold text-gray-800 mb-6">Admin Dashboard</h2>
          {error && <p className="text-red-500 mb-4">{error}</p>}
          {success && <p className="text-green-500 mb-4">{success}</p>}

          {activeSection === 'dashboard' && (
            <section>
              <h3 className="text-xl font-semibold text-gray-800 mb-4">Dashboard</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
                <div className="bg-blue-100 p-4 rounded-lg shadow">
                  <h4 className="text-lg font-medium text-gray-700">Last 30 Days Visitors</h4>
                  <p className="text-2xl font-bold text-blue-600">{last30DaysVisits.length}</p>
                </div>
              </div>
              <h4 className="text-lg font-semibold text-gray-800 mb-4">Visitors Over Last 30 Days</h4>
              <div className="max-w-2xl mb-6">
                <Bar data={chartData} options={chartOptions} />
              </div>
              <h4 className="text-lg font-semibold text-gray-800 mb-4">Recent Visitors</h4>
              {loading ? <p className="text-gray-600">Loading visits...</p> : (
                <table className="w-full table-auto border-collapse">
                  <thead>
                    <tr className="bg-gray-200">
                      <th className="px-4 py-2 text-left text-gray-700">Visitor Name</th>
                      <th className="px-4 py-2 text-left text-gray-700">Host Name</th>
                      <th className="px-4 py-2 text-left text-gray-700">Check-In Time</th>
                    </tr>
                  </thead>
                  <tbody>
                    {last30DaysVisits.slice(0, 5).map(visit => (
                      <tr key={visit.id} className="hover:bg-gray-50">
                        <td className="px-4 py-2 border-b">{visit.visitorName}</td>
                        <td className="px-4 py-2 border-b">{visit.hostName}</td>
                        <td className="px-4 py-2 border-b">{new Date(visit.check_in_time).toLocaleString()}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </section>
          )}

          {activeSection === 'users' && (
            <section>
              <h3 className="text-xl font-semibold text-gray-800 mb-4">Create Host User</h3>
              <p className="text-gray-600 italic mb-4">Note: All created users are assigned the 'host' role.</p>
              <div className="bg-gray-50 p-6 rounded-lg shadow mb-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <label className="flex flex-col">
                    <span className="text-gray-700 font-medium mb-2">Full Name</span>
                    <input 
                      type="text" 
                      name="name" 
                      value={newUser.name} 
                      onChange={handleUserInputChange} 
                      required 
                      className="border border-gray-300 rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Enter full name"
                    />
                  </label>
                  <label className="flex flex-col">
                    <span className="text-gray-700 font-medium mb-2">Email</span>
                    <input 
                      type="email" 
                      name="email" 
                      value={newUser.email} 
                      onChange={handleUserInputChange} 
                      required 
                      className="border border-gray-300 rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Enter email"
                    />
                  </label>
                  <label className="flex flex-col">
                    <span className="text-gray-700 font-medium mb-2">Password</span>
                    <input 
                      type="password" 
                      name="password" 
                      value={newUser.password} 
                      onChange={handleUserInputChange} 
                      required 
                      className="border border-gray-300 rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Enter password"
                    />
                  </label>
                  <div className="flex items-end">
                    <button 
                      type="submit" 
                      onClick={handleCreateUser}
                      className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 transition"
                    >
                      Create Host
                    </button>
                  </div>
                </div>
              </div>
              <h3 className="text-xl font-semibold text-gray-800 mb-4">Existing Users</h3>
              {loading ? <p className="text-gray-600">Loading users...</p> : (
                <table className="w-full table-auto border-collapse">
                  <thead>
                    <tr className="bg-gray-200">
                      <th className="px-4 py-2 text-left text-gray-700">Name</th>
                      <th className="px-4 py-2 text-left text-gray-700">Email</th>
                      <th className="px-4 py-2 text-left text-gray-700">Role</th>
                    </tr>
                  </thead>
                  <tbody>
                    {users.length === 0 ? (
                      <tr><td colSpan="3" className="px-4 py-2 text-center text-gray-600 border-b">No users found</td></tr>
                    ) : (
                      users.map(user => (
                        <tr key={user.id} className="hover:bg-gray-50">
                          <td className="px-4 py-2 border-b">{user.name}</td>
                          <td className="px-4 py-2 border-b">{user.email}</td>
                          <td className="px-4 py-2 border-b">{user.role}</td>
                        </tr>
                      ))
                    )}
                  </tbody>
                </table>
              )}
            </section>
          )}

          {activeSection === 'visitors' && (
            <section className="admin-dashboard-section">
              <h3 className="text-xl font-semibold text-gray-800 mb-4">Visitor Logs</h3>
              <div className="bg-gray-50 p-6 rounded-lg shadow mb-6">
                <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                  <label className="flex flex-col">
                    <span className="text-gray-700 font-medium mb-2">Start Date</span>
                    <input 
                      type="date" 
                      name="startDate" 
                      value={filters.startDate} 
                      onChange={handleFilterChange} 
                      className="border border-gray-300 rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </label>
                  <label className="flex flex-col">
                    <span className="text-gray-700 font-medium mb-2">End Date</span>
                    <input 
                      type="date" 
                      name="endDate" 
                      value={filters.endDate} 
                      onChange={handleFilterChange} 
                      className="border border-gray-300 rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </label>
                  <label className="flex flex-col">
                    <span className="text-gray-700 font-medium mb-2">Host Name</span>
                    <input 
                      name="hostName" 
                      value={filters.hostName} 
                      onChange={handleFilterChange} 
                      className="border border-gray-300 rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Enter host name"
                    />
                  </label>
                  <label className="flex flex-col">
                    <span className="text-gray-700 font-medium mb-2">Visitor Name</span>
                    <input 
                      name="visitorName" 
                      value={filters.visitorName} 
                      onChange={handleFilterChange} 
                      className="border border-gray-300 rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Enter visitor name"
                    />
                  </label>
                </div>
                <button 
                  type="submit" 
                  onClick={handleFilterSubmit}
                  className="mt-4 bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 transition"
                >
                  Filter
                </button>
              </div>
              {loading ? <p className="text-gray-600">Loading visits...</p> : (
                <table className="w-full table-auto border-collapse">
                  <thead>
                    <tr className="bg-gray-200">
                      <th className="px-4 py-2 text-left text-gray-700">Visitor Name</th>
                      <th className="px-4 py-2 text-left text-gray-700">Visitor Email</th>
                      <th className="px-4 py-2 text-left text-gray-700">Photo</th>
                      <th className="px-4 py-2 text-left text-gray-700">Host Name</th>
                      <th className="px-4 py-2 text-left text-gray-700">Reason</th>
                      <th className="px-4 py-2 text-left text-gray-700">Check-In Time</th>
                      <th className="px-4 py-2 text-left text-gray-700">Check-Out Time</th>
                    </tr>
                  </thead>
                  <tbody>
                    {visits.length === 0 ? (
                      <tr><td colSpan="7" className="px-4 py-2 text-center text-gray-600 border-b">No visits found for the selected filters.</td></tr>
                    ) : (
                      visits.map(visit => (
                        <tr key={visit.id} className="hover:bg-gray-50">
                          <td className="px-4 py-2 border-b">{visit.visitorName}</td>
                          <td className="px-4 py-2 border-b">{visit.visitorEmail}</td>
                          <td className="px-4 py-2 border-b">
                            {visit.visitorPhoto && <img src={visit.visitorPhoto} alt="Visitor" className="max-w-[80px] rounded" />}
                          </td>
                          <td className="px-4 py-2 border-b">{visit.hostName}</td>
                          <td className="px-4 py-2 border-b">{visit.reason}</td>
                          <td className="px-4 py-2 border-b">{new Date(visit.check_in_time).toLocaleString()}</td>
                          <td className="px-4 py-2 border-b">{visit.check_out_time ? new Date(visit.check_out_time).toLocaleString() : 'Not Checked Out'}</td>
                        </tr>
                      ))
                    )}
                  </tbody>
                </table>
              )}
            </section>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboardPage;
