import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import VisitorCheckInPage from './pages/VisitorCheckInPage';
import HostDashboardPage from './pages/HostDashboardPage';
import AdminDashboardPage from './pages/AdminDashboardPage';
import HomePage from './pages/HomePage';
import ProductPage from './pages/ProductPage';
import ResourcePage from './pages/ResourcePage';
import AboutUsPage from './pages/AboutUsPage';
import BookDemoPage from './pages/BookDemoPage';
import ContactUsPage from './pages/ContactUsPage';
import RegistrationPage from './pages/RegistrationPage';
import './App.css';

function App() {
  // Simple auth check from localStorage token
  const token = localStorage.getItem('token');
  const userRole = localStorage.getItem('role');


  if (!token) {
    return (
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/login" element={<LoginPage />} />
        <Route path="/register" element={<RegistrationPage />} />
        <Route path="/products" element={<ProductPage />} />
        <Route path="/resources" element={<ResourcePage />} />
        <Route path="/aboutus" element={<AboutUsPage />} />
        <Route path="/bookademo" element={<BookDemoPage />} />
        <Route path="/contactus" element={<ContactUsPage />} />
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    );
  }

  return (
    <Routes>
      <Route path="/checkin" element={<VisitorCheckInPage />} />
      {userRole === 'host' && <Route path="/host" element={<HostDashboardPage />} />}
      {userRole === 'admin' && <Route path="/admin" element={<AdminDashboardPage />} />}
      <Route path="*" element={<Navigate to={userRole === 'admin' ? '/admin' : '/host'} replace />} />
    </Routes>
  );
}

export default App;
